import { getDb, ANONYMOUS_USER_ID } from "./db";
import type { Conversation } from "@/app/domain";

interface ConversationRow {
  id: string;
  user_id: string;
  title: string;
  created_at: string;
  updated_at: string;
}

function toConversation(row: ConversationRow): Conversation {
  return {
    id: row.id,
    userId: row.user_id,
    title: row.title,
    createdAt: row.created_at,
    updatedAt: row.updated_at,
  };
}

export async function listConversations(): Promise<Conversation[]> {
  const { data, error } = await getDb()
    .from("conversations")
    .select("*")
    .eq("user_id", ANONYMOUS_USER_ID)
    .order("updated_at", { ascending: false });

  if (error) throw error;
  return (data as ConversationRow[]).map(toConversation);
}

export async function getConversation(
  id: string,
): Promise<Conversation | null> {
  const { data, error } = await getDb()
    .from("conversations")
    .select("*")
    .eq("id", id)
    .single();

  if (error) {
    if (error.code === "PGRST116") return null; // not found
    throw error;
  }
  return toConversation(data as ConversationRow);
}

export async function createConversation(
  title?: string,
): Promise<Conversation> {
  const { data, error } = await getDb()
    .from("conversations")
    .insert({ user_id: ANONYMOUS_USER_ID, title: title ?? "" })
    .select()
    .single();

  if (error) throw error;
  return toConversation(data as ConversationRow);
}

export async function updateConversationTitle(
  id: string,
  title: string,
): Promise<void> {
  const { error } = await getDb()
    .from("conversations")
    .update({ title })
    .eq("id", id);

  if (error) throw error;
}

export async function deleteConversation(id: string): Promise<void> {
  const { error } = await getDb()
    .from("conversations")
    .delete()
    .eq("id", id);

  if (error) throw error;
}

