-- ============================================================
-- 1. conversations
-- ============================================================
create table conversations (
  id          uuid primary key default gen_random_uuid(),
  user_id     uuid not null,  -- auth.users FKëŠ” ì¸ì¦ êµ¬í˜„ ì‹œ ì¶”ê°€
  title       text not null default '',
  created_at  timestamptz not null default now(),
  updated_at  timestamptz not null default now()
);

create index idx_conversations_user_id on conversations(user_id);

-- ============================================================
-- 2. messages
-- ============================================================
-- type: user | assistant | artifact | proposal | status
create table messages (
  id                uuid primary key default gen_random_uuid(),
  conversation_id   uuid not null references conversations(id) on delete cascade,
  type              text not null check (type in ('user', 'assistant', 'artifact', 'proposal', 'status')),
  content           jsonb not null default '{}'::jsonb,
  created_at        timestamptz not null default now()
);

create index idx_messages_conversation_id on messages(conversation_id);
create index idx_messages_created_at on messages(conversation_id, created_at);

-- ============================================================
-- 3. artifacts
-- ============================================================
-- type: paper_card | paper_collection | analysis_report | researcher_profile | research_note
-- status: creating | ready | updating | archived
create table artifacts (
  id                uuid primary key default gen_random_uuid(),
  conversation_id   uuid not null references conversations(id) on delete cascade,
  type              text not null check (type in ('paper_card', 'paper_collection', 'analysis_report', 'researcher_profile', 'research_note')),
  status            text not null default 'creating' check (status in ('creating', 'ready', 'updating', 'archived')),
  data              jsonb not null default '{}'::jsonb,
  parent_id         uuid references artifacts(id) on delete set null,
  "references"      text[] not null default '{}',
  created_at        timestamptz not null default now(),
  updated_at        timestamptz not null default now()
);

create index idx_artifacts_conversation_id on artifacts(conversation_id);
create index idx_artifacts_parent_id on artifacts(parent_id);

-- ============================================================
-- 4. research_journeys (ê²½í—˜ ê¸°ì–µ)
-- ============================================================
create table research_journeys (
  id                uuid primary key default gen_random_uuid(),
  conversation_id   uuid not null unique references conversations(id) on delete cascade,
  user_id           uuid not null,  -- auth.users FKëŠ” ì¸ì¦ êµ¬í˜„ ì‹œ ì¶”ê°€
  memories          jsonb not null default '[]'::jsonb,
  created_at        timestamptz not null default now()
);

create index idx_research_journeys_user_id on research_journeys(user_id);

-- ============================================================
-- 5. updated_at ìë™ ê°±ì‹  íŠ¸ë¦¬ê±°
-- ============================================================
create or replace function update_updated_at()
returns trigger as $$
begin
  new.updated_at = now();
  return new;
end;
$$ language plpgsql;

create trigger trg_conversations_updated_at
  before update on conversations
  for each row execute function update_updated_at();

create trigger trg_artifacts_updated_at
  before update on artifacts
  for each row execute function update_updated_at();

-- ============================================================
-- 6. RLS (Row Level Security)
-- ============================================================
alter table conversations enable row level security;
alter table messages enable row level security;
alter table artifacts enable row level security;
alter table research_journeys enable row level security;

