import { google } from "@ai-sdk/google";
import {
  streamText,
  type UIMessage,
  convertToModelMessages,
  stepCountIs,
} from "ai";
import { buildSystemPrompt } from "@/app/server/agent/system-prompt";
import { createTools } from "@/app/server/agent/tools";
import { buildMemoryPrompt } from "@/app/server/agent/memory";
import {
  appendMessage,
  updateConversationTitle,
  listRecentJourneys,
  listConversations,
} from "@/app/server/repository";

export const maxDuration = 120;

export async function POST(req: Request) {
  const {
    messages,
    conversationId,
  }: { messages: UIMessage[]; conversationId?: string } = await req.json();

  if (!conversationId) {
    return new Response("conversationId is required", { status: 400 });
  }

  // ë§ˆì§€ë§‰ ì‚¬ìš©ì ë©”ì‹œì§€ë¥¼ DBì— ì €ì¥
  const lastMessage = messages[messages.length - 1];
  if (lastMessage?.role === "user") {
    const text =
      lastMessage.parts
        ?.filter((p): p is { type: "text"; text: string } => p.type === "text")
        .map((p) => p.text)
        .join("") || "";

    if (text) {
      await appendMessage({
        conversationId,
        type: "user",
        text,
      });

      // ì²« ë©”ì‹œì§€ì´ë©´ ëŒ€í™” ì œëª©ì„ ìë™ ì„¤ì •
      if (messages.length === 1) {
        const title = text.slice(0, 50) + (text.length > 50 ? "â€¦" : "");
        await updateConversationTitle(conversationId, title);
      }
    }
  }

  // ìµœê·¼ ì—°êµ¬ ì—¬ì • ê¸°ì–µì„ ì‹œìŠ¤í…œ í”„ë¡¬í”„íŠ¸ì— ì£¼ì…
  const [recentJourneys, conversations] = await Promise.all([
    listRecentJourneys(5),
    listConversations(),
  ]);

  // í˜„ì¬ ëŒ€í™”ì˜ ê¸°ì–µì€ ì œì™¸ (í˜„ì¬ ì„¸ì…˜ ë§¥ë½ì€ ëŒ€í™” íˆìŠ¤í† ë¦¬ì— ìˆìœ¼ë¯€ë¡œ)
  const otherJourneys = recentJourneys.filter(
    (j) => j.conversationId !== conversationId,
  );

  let memorySection: string | undefined;
  if (otherJourneys.length > 0) {
    const titleMap = new Map(conversations.map((c) => [c.id, c.title]));
    memorySection = buildMemoryPrompt(otherJourneys, titleMap);
  }

  const systemPrompt = buildSystemPrompt(memorySection);

  const result = streamText({
    model: google("gemini-3-flash-preview"),
    system: systemPrompt,
    messages: await convertToModelMessages(messages),
    tools: createTools(conversationId),
    stopWhen: stepCountIs(10),
    onFinish: async ({ text, steps }) => {
      // ëª¨ë“  stepì˜ toolResultsì—ì„œ ë„êµ¬ í˜¸ì¶œ ì •ë³´ ì¶”ì¶œ
      const toolParts = steps.flatMap((step) =>
        step.toolResults.map((tr) => ({
          toolName: tr.toolName,
          toolCallId: tr.toolCallId,
          input: tr.input,
          output: tr.output,
        })),
      );

      if (text || toolParts.length > 0) {
        await appendMessage({
          conversationId,
          type: "assistant",
          text: text ?? "",
          toolParts: toolParts.length > 0 ? toolParts : undefined,
        });
      }
    },
  });

  return result.toUIMessageStreamResponse();
}

/**
 * ê²½í—˜ ê¸°ì–µ ì‹œìŠ¤í…œ â€” ëŒ€í™”ì—ì„œ í•µì‹¬ ê¸°ì–µì„ ì¶”ì¶œí•˜ê³ , ì‹œìŠ¤í…œ í”„ë¡¬í”„íŠ¸ì— ì£¼ì…í•œë‹¤.
 * architecture.md Â§6 ê¸°ì–µ ì‹œìŠ¤í…œ, SPEC.md Â§7 ê²½í—˜ ê¸°ì–µ
 */

import { getGeminiClient, GEMINI_MODEL } from "@/app/lib/gemini";
import type { Memory, ResearchJourney } from "@/app/domain";
import type { PersistedMessage } from "@/app/server/repository/messages";

const EXTRACTION_PROMPT = `\
ë„ˆëŠ” í•™ìˆ  ì—°êµ¬ ëŒ€í™” ë¶„ì„ê°€ë‹¤. ì•„ë˜ ëŒ€í™”ì—ì„œ í•µì‹¬ ê¸°ì–µ 4ê°€ì§€ë¥¼ ì¶”ì¶œí•˜ë¼.

## ê¸°ì–µ ìœ í˜•

1. **exploration** (íƒìƒ‰): ì–´ë–¤ ì£¼ì œ/í‚¤ì›Œë“œë¥¼ ì°¾ì•„ë´¤ëŠ”ê°€
2. **discovery** (ë°œê²¬): ì–´ë–¤ ì˜ë¯¸ ìˆëŠ” ê²°ê³¼ë¥¼ ì°¾ì•˜ëŠ”ê°€
3. **judgment** (íŒë‹¨): ì‚¬ìš©ìê°€ ì–´ë–¤ ê²°ì •ì„ ë‚´ë ¸ëŠ”ê°€
4. **pivot** (ë°©í–¥ ì „í™˜): ì–´ë””ì„œ íƒìƒ‰ ë°©í–¥ì„ ë°”ê¿¨ëŠ”ê°€

## ê·œì¹™

- ê° ìœ í˜•ë³„ë¡œ 0~3ê°œì˜ ê¸°ì–µì„ ì¶”ì¶œí•œë‹¤ (í•´ë‹¹ ì—†ìœ¼ë©´ 0ê°œ)
- contentëŠ” í•œêµ­ì–´ë¡œ, í•œ ë¬¸ì¥ìœ¼ë¡œ í•µì‹¬ë§Œ ë‹´ëŠ”ë‹¤
- ëŒ€í™”ê°€ ë„ˆë¬´ ì§§ê±°ë‚˜ ì˜ë¯¸ ìˆëŠ” ë‚´ìš©ì´ ì—†ìœ¼ë©´ ë¹ˆ ë°°ì—´ì„ ë°˜í™˜í•œë‹¤
- ë°˜ë“œì‹œ ì•„ë˜ JSON í˜•ì‹ìœ¼ë¡œë§Œ ì‘ë‹µí•œë‹¤

## ì‘ë‹µ í˜•ì‹

\`\`\`json
[
  { "type": "exploration", "content": "..." },
  { "type": "discovery", "content": "..." },
  { "type": "judgment", "content": "..." },
  { "type": "pivot", "content": "..." }
]
\`\`\`

## ëŒ€í™” ë‚´ìš©

`;

/**
 * ëŒ€í™” ë©”ì‹œì§€ì—ì„œ í•µì‹¬ ê¸°ì–µì„ ì¶”ì¶œí•œë‹¤.
 * Geminië¥¼ í˜¸ì¶œí•˜ì—¬ 4ê°€ì§€ ìœ í˜•ì˜ ê¸°ì–µì„ ë¶„ì„í•œë‹¤.
 */
export async function extractMemories(
  messages: PersistedMessage[],
): Promise<Memory[]> {
  if (messages.length < 2) return [];

  const conversationText = messages
    .map((m) => `[${m.role}] ${m.content}`)
    .join("\n\n");

  const client = getGeminiClient();
  const response = await client.models.generateContent({
    model: GEMINI_MODEL,
    contents: EXTRACTION_PROMPT + conversationText,
  });

  const text = response.text?.trim();
  if (!text) return [];

  try {
    // JSON ë¸”ë¡ ì¶”ì¶œ (```json ... ``` ë˜ëŠ” ë°”ë¡œ JSON)
    const jsonMatch = text.match(/\[[\s\S]*\]/);
    if (!jsonMatch) return [];

    const parsed = JSON.parse(jsonMatch[0]) as Array<{
      type: string;
      content: string;
    }>;

    const now = new Date().toISOString();
    return parsed
      .filter(
        (m) =>
          ["exploration", "discovery", "judgment", "pivot"].includes(m.type) &&
          m.content,
      )
      .map((m) => ({
        type: m.type as Memory["type"],
        content: m.content,
        relatedArtifactIds: [],
        timestamp: now,
      }));
  } catch {
    return [];
  }
}

const MEMORY_TYPE_ICONS: Record<Memory["type"], string> = {
  exploration: "ğŸ”",
  discovery: "ğŸ“Š",
  judgment: "ğŸ“",
  pivot: "ğŸ”€",
};

const MEMORY_TYPE_LABELS: Record<Memory["type"], string> = {
  exploration: "íƒìƒ‰",
  discovery: "ë°œê²¬",
  judgment: "íŒë‹¨",
  pivot: "ë°©í–¥ ì „í™˜",
};

/**
 * ìµœê·¼ ì—°êµ¬ ì—¬ì •ë“¤ì„ ì‹œìŠ¤í…œ í”„ë¡¬í”„íŠ¸ì— ì£¼ì…í•  í…ìŠ¤íŠ¸ë¡œ ë³€í™˜í•œë‹¤.
 * ëŒ€í™” ì œëª©ì´ ìˆìœ¼ë©´ í•¨ê»˜ í‘œì‹œí•œë‹¤.
 */
export function buildMemoryPrompt(
  journeys: ResearchJourney[],
  conversationTitles?: Map<string, string>,
): string {
  if (journeys.length === 0) return "";

  const lines = journeys.map((j) => {
    const title =
      conversationTitles?.get(j.conversationId) ?? "ì´ì „ ì—°êµ¬";
    const date = new Date(j.createdAt).toLocaleDateString("ko-KR", {
      month: "short",
      day: "numeric",
    });

    const memoryLines = j.memories
      .map(
        (m) =>
          `  ${MEMORY_TYPE_ICONS[m.type]} ${MEMORY_TYPE_LABELS[m.type]}: ${m.content}`,
      )
      .join("\n");

    return `- [${date}] ${title}\n${memoryLines}`;
  });

  return `\
## ì´ì „ ì—°êµ¬ ì—¬ì •

ë„ˆëŠ” ì´ ì‚¬ìš©ìì™€ í•¨ê»˜ ì—°êµ¬ë¥¼ í•´ì˜¨ ë™ë£Œë‹¤. ì•„ë˜ëŠ” ì´ì „ ì„¸ì…˜ì—ì„œ ì¶•ì ëœ ê¸°ì–µì´ë‹¤.
ì´ ê¸°ì–µì„ ìì—°ìŠ¤ëŸ½ê²Œ í™œìš©í•˜ë˜, ì–µì§€ë¡œ ì–¸ê¸‰í•˜ì§€ëŠ” ì•ŠëŠ”ë‹¤.
ê´€ë ¨ ì£¼ì œê°€ ë‚˜ì˜¤ë©´ "ì§€ë‚œë²ˆì— ..." ì‹ìœ¼ë¡œ ì´ì „ ë§¥ë½ì„ ì—°ê²°í•œë‹¤.
ì‚¬ìš©ìê°€ "ì´ì–´ì„œ í•˜ê³  ì‹¶ë‹¤"ê³  í•˜ë©´ ì´ì „ ê¸°ì–µì„ ë°”íƒ•ìœ¼ë¡œ ì—°êµ¬ ë°©í–¥ì„ ì œì•ˆí•œë‹¤.

${lines.join("\n\n")}
`;
}
