 3â†’import { useState, useEffect, useCallback } from "react";
 4â†’import { useRouter, usePathname } from "next/navigation";
 5â†’import { NoteSidebar, UndoToast } from "@/app/features/notes";
 6â†’import { useSessionStore } from "@/app/stores/sessionStore";
 7â†’
 8â†’export default function MainLayout({ children }: { children: React.ReactNode }) {
 9â†’  const sessions = useSessionStore((s) => s.sessions);
10â†’  const currentSessionId = useSessionStore((s) => s.currentSessionId);
11â†’  const initialize = useSessionStore((s) => s.initialize);
12â†’  const createSession = useSessionStore((s) => s.createSession);
13â†’  const renameSession = useSessionStore((s) => s.renameSession);
14â†’  const deleteSession = useSessionStore((s) => s.deleteSession);
15â†’  const switchSession = useSessionStore((s) => s.switchSession);
16â†’  const undoState = useSessionStore((s) => s.undoState);
17â†’  const onUndo = useSessionStore((s) => s.onUndo);
18â†’  const onDismissUndo = useSessionStore((s) => s.onDismissUndo);
19â†’
20â†’  const router = useRouter();
21â†’  const pathname = usePathname();
22â†’
23â†’  const currentSession =
24â†’    sessions.find((s) => s.id === currentSessionId) ?? null;
25â†’  const canCreate = sessions.length < 10;
26â†’
27â†’  const [sidebarCollapsed, setSidebarCollapsed] = useState(false);
28â†’
29â†’  const handleSwitchSession = useCallback((id: string) => {
30â†’    switchSession(id);
31â†’    if (pathname.startsWith("/explore/")) {
32â†’      router.push(`/explore/${id}`);
33â†’    }
34â†’  }, [switchSession, pathname, router]);
35â†’
36â†’  useEffect(() => {
37â†’    initialize();
38â†’  }, [initialize]);
39â†’
40â†’  return (
41â†’    <>
42â†’      <NoteSidebar
43â†’        sessions={sessions}
44â†’        currentSessionId={currentSession?.id ?? null}
45â†’        canCreate={canCreate}
46â†’        onCreateSession={createSession}
47â†’        onSwitchSession={handleSwitchSession}
48â†’        onRenameSession={renameSession}
49â†’        onDeleteSession={deleteSession}
50â†’        isCollapsed={sidebarCollapsed}
51â†’        onToggleCollapse={() => setSidebarCollapsed(!sidebarCollapsed)}
52â†’      />
53â†’      <main className="flex flex-1 flex-col overflow-hidden">
54â†’        {currentSession ? (
55â†’          <>
56â†’            <div className="flex h-14 items-center justify-between border-b border-card-border px-6">
57â†’              <span className="text-sm font-medium">{currentSession.name}</span>
58â†’            </div>
59â†’            {children}
60â†’          </>
61â†’        ) : (
62â†’          <div className="flex flex-1 items-center justify-center">
63â†’            <div className="h-5 w-5 animate-spin rounded-full border-2 border-muted-foreground/30 border-t-accent" />
64â†’          </div>
65â†’        )}
66â†’      </main>
67â†’      {undoState && (
68â†’        <UndoToast
69â†’          message={undoState.message}
70â†’          countdown={undoState.countdown}
71â†’          onUndo={onUndo}
72â†’          onDismiss={onDismissUndo}
73â†’        />
74â†’      )}
75â†’    </>
76â†’  );
77â†’}
