 16â†’  message: string;
 17â†’  countdown: number;
 18â†’  session: Session;
 19â†’}
 20â†’
 21â†’export interface SessionState {
 22â†’  sessions: Session[];
 23â†’  currentSessionId: string | null;
 24â†’  undoState: { message: string; countdown: number } | null;
 25â†’
 26â†’  initialize: () => void;
 27â†’  createSession: () => Session | null;
 28â†’  renameSession: (id: string, name: string) => void;
 29â†’  deleteSession: (id: string) => void;
 30â†’  switchSession: (id: string) => void;
 31â†’  updateSession: (id: string, patch: Partial<Session>) => void;
 32â†’  onUndo: () => void;
 33â†’  onDismissUndo: () => void;
 34â†’}
 35â†’
 36â†’// Undo timer state (module-scope, not serialized)
 37â†’let undoTimer: ReturnType<typeof setInterval> | null = null;
 38â†’let undoData: UndoData | null = null;
 39â†’
 40â†’function cleanupUndo() {
 41â†’  if (undoTimer) {
 42â†’    clearInterval(undoTimer);
 43â†’    undoTimer = null;
 44â†’  }
 45â†’  undoData = null;
 46â†’}
 47â†’
 48â†’function syncSessionList(sessions: Session[], activeId: string | null) {
 49â†’  saveSessionList({
 50â†’    activeSessionId: activeId,
 51â†’    sessions: sessions.map(toSessionMeta),
 52â†’    schemaVersion: 1,
 53â†’  });
 54â†’}
 55â†’
 56â†’export const useSessionStore = create<SessionState>((set, get) => ({
 57â†’  sessions: [],
 58â†’  currentSessionId: null,
 59â†’  undoState: null,
 60â†’
 61â†’  initialize: () => {
 62â†’    const listState = loadSessionList();
 63â†’    const sessionIds = listState?.sessions.map((s) => s.id) ?? [];
 64â†’    const loaded = sessionIds
 65â†’      .map((id) => loadSession(id))
 66â†’      .filter((s): s is Session => s !== null);
 67â†’
 68â†’    if (loaded.length > 0) {
 69â†’      set({
 70â†’        sessions: loaded,
 71â†’        currentSessionId: listState?.activeSessionId ?? loaded[0].id,
 72â†’      });
 73â†’    } else {
