import { NextResponse } from "next/server";
import { SemanticScholarResponseSchema } from "@/app/lib/schemas";
import { withAPIObservation } from "@/app/lib/observe";

const S2_API_URL = "https://api.semanticscholar.org/graph/v1/paper/search";
const FIELDS = "paperId,title,abstract,year,citationCount,url,authors";
const MIN_REQUEST_INTERVAL = 100;

let lastRequestTime = 0;

async function throttle(): Promise<void> {
  const now = Date.now();
  const elapsed = now - lastRequestTime;
  if (elapsed < MIN_REQUEST_INTERVAL) {
    await new Promise((r) => setTimeout(r, MIN_REQUEST_INTERVAL - elapsed));
  }
  lastRequestTime = Date.now();
}

export const GET = withAPIObservation("/api/search", async (request) => {
  const { searchParams } = request.nextUrl;
  const query = searchParams.get("query");
  const limit = searchParams.get("limit") ?? "20";
  const offset = searchParams.get("offset") ?? "0";

  if (!query) {
    return NextResponse.json({ error: "query is required" }, { status: 400 });
  }

  await throttle();

  const url = new URL(S2_API_URL);
  url.searchParams.set("query", query);
  url.searchParams.set("limit", limit);
  url.searchParams.set("offset", offset);
  url.searchParams.set("fields", FIELDS);

  const headers: Record<string, string> = {};
  const apiKey = process.env.S2_API_KEY;
  if (apiKey) {
    headers["x-api-key"] = apiKey;
  }

  const res = await fetch(url.toString(), { headers });

  if (!res.ok) {
    return NextResponse.json(
      { error: `Semantic Scholar API error: ${res.status}` },
      { status: res.status }
    );
  }

  const raw = await res.json();
  const parsed = SemanticScholarResponseSchema.safeParse(raw);

  if (!parsed.success) {
    return NextResponse.json(
      { error: "Invalid API response", details: parsed.error.issues },
      { status: 502 }
    );
  }

  return NextResponse.json(parsed.data);
});

