import { NextResponse } from "next/server";
import { GeminiAnalysisSchema } from "@/app/lib/schemas";
import { getGeminiClient, GEMINI_MODEL } from "@/app/lib/gemini";
import { withAIObservation, withAPIObservation } from "@/app/lib/observe";

const PROMPT = `ë‹¹ì‹ ì€ í•™ìˆ  ë…¼ë¬¸ ë¶„ì„ ì „ë¬¸ê°€ì…ë‹ˆë‹¤.
ì•„ë˜ ë…¼ë¬¸ì˜ abstractë¥¼ ë¶„ì„í•˜ì—¬ í•œêµ­ì–´ë¡œ êµ¬ì¡°í™”ëœ ìš”ì•½ì„ ìƒì„±í•˜ì„¸ìš”.

[Abstract]
{abstract}

ë°˜ë“œì‹œ ì•„ë˜ JSON í˜•ì‹ìœ¼ë¡œë§Œ ì‘ë‹µí•˜ì„¸ìš” (ë‹¤ë¥¸ í…ìŠ¤íŠ¸ ì—†ì´):
{
  "summary": "ì—°êµ¬ì˜ ì „ì²´ì  ë‚´ìš© (2-3ë¬¸ì¥)",
  "objective": "ì—°êµ¬ê°€ ë‹¬ì„±í•˜ê³ ì í•˜ëŠ” ê²ƒ (1-2ë¬¸ì¥)",
  "methodology": "ì‚¬ìš©ëœ ì—°êµ¬ ë°©ë²• (1-2ë¬¸ì¥)",
  "results": "ì£¼ìš” ë°œê²¬ ë° ì„±ê³¼ (1-2ë¬¸ì¥)",
  "keywords": ["í‚¤ì›Œë“œ1", "í‚¤ì›Œë“œ2", "í‚¤ì›Œë“œ3"],
  "evidenceMap": {
    "summary": "ê·¼ê±°ê°€ ëœ abstract ì›ë¬¸ êµ¬ì ˆ",
    "objective": "ê·¼ê±°ê°€ ëœ abstract ì›ë¬¸ êµ¬ì ˆ",
    "methodology": "ê·¼ê±°ê°€ ëœ abstract ì›ë¬¸ êµ¬ì ˆ",
    "results": "ê·¼ê±°ê°€ ëœ abstract ì›ë¬¸ êµ¬ì ˆ"
  }
}`;

export const POST = withAPIObservation("/api/analyze", async (request) => {
  const { abstract } = await request.json();

  if (!abstract) {
    return NextResponse.json(
      { error: "abstract is required" },
      { status: 400 }
    );
  }

  const prompt = PROMPT.replace("{abstract}", abstract);

  const response = await withAIObservation("analyze", {}, () =>
    getGeminiClient().models.generateContent({
      model: GEMINI_MODEL,
      contents: prompt,
      config: {
        responseMimeType: "application/json",
      },
    })
  );

  const text = response.text ?? "";

  try {
    const json = JSON.parse(text);
    const parsed = GeminiAnalysisSchema.safeParse(json);

    if (!parsed.success) {
      return NextResponse.json(
        { error: "Invalid analysis response", details: parsed.error.issues },
        { status: 502 }
      );
    }

    return NextResponse.json(parsed.data);
  } catch {
    return NextResponse.json(
      { error: "Failed to parse AI response" },
      { status: 502 }
    );
  }
