import { GoogleGenAI } from "@google/genai";

export const GEMINI_MODEL = "gemini-3-flash-preview";

let _client: GoogleGenAI | null = null;

export function getGeminiClient(): GoogleGenAI {
  if (!_client) {
    _client = new GoogleGenAI({ apiKey: process.env.GEMINI_API_KEY! });
  }
  return _client;
}

/**
 * abstractë¥¼ 200ìë¡œ ìë¥´ê³  "..."ì„ ë¶™ì¸ë‹¤.
 * nullì´ë©´ "(no abstract)"ë¥¼ ë°˜í™˜.
 */
export function truncateAbstract(
  abstract: string | null,
  maxLength = 200
): string {
  if (!abstract) return "(no abstract)";
  if (abstract.length <= maxLength) return abstract;
  return abstract.slice(0, maxLength) + "...";
}

/**
 * ë…¼ë¬¸ ë°°ì—´ë¡œë¶€í„° ì¸ë±ìŠ¤â†’paperId ë§¤í•‘ í…Œì´ë¸”ê³¼
 * í”„ë¡¬í”„íŠ¸ìš© í…ìŠ¤íŠ¸ ëª©ë¡ì„ ìƒì„±í•œë‹¤.
 */
export function buildPaperIndex(
  papers: { paperId: string; title: string; abstract: string | null }[],
  opts?: {
    /** abstractë¥¼ ì˜ë¼ì„œ í¬í•¨í•  ìµœëŒ€ ê¸¸ì´. undefinedë©´ ì „ì²´ í¬í•¨ */
    truncateAt?: number;
    /** ì¶”ê°€ í•„ë“œ í¬ë§·í„° (ì˜ˆ: ì—°ë„, ì¸ìš©ìˆ˜ ë“±) */
    formatExtra?: (p: { paperId: string; title: string; abstract: string | null }, i: number) => string;
  }
): { idMap: Map<string, string>; paperList: string } {
  const idMap = new Map<string, string>();

  const lines = papers.map((p, i) => {
    idMap.set(String(i), p.paperId);
    const extra = opts?.formatExtra?.(p, i) ?? "";
    const abstractText =
      opts?.truncateAt != null
        ? truncateAbstract(p.abstract, opts.truncateAt)
        : p.abstract ?? "(no abstract)";
    return `- [${i}] ${p.title}${extra}\n  ${abstractText}`;
  });

  return { idMap, paperList: lines.join("\n") };
}

/**
 * AI ì‘ë‹µì˜ ì¸ë±ìŠ¤ ë¬¸ìì—´ì„ ì‹¤ì œ paperIdë¡œ ì¹˜í™˜í•œë‹¤.
 */
export function remapIndices(
  indices: string[],
  idMap: Map<string, string>
): string[] {
  return indices
    .map((idx) => idMap.get(idx))
    .filter((id): id is string => id !== undefined);
}

