      model: GEMINI_MODEL,
      contents: prompt,
      config: {
        responseMimeType: "application/json",
      },
    })
  );

  const text = response.text ?? "";

  try {
    const json = JSON.parse(text);
    const parsed = GeminiClusterSchema.safeParse(json);

    if (!parsed.success) {
      return NextResponse.json(
        { error: "Invalid cluster response", details: parsed.error.issues },
        { status: 502 }
      );
    }

    // ì¸ë±ìŠ¤ â†’ ì‹¤ì œ paperId ì¹˜í™˜
    const data = parsed.data;
    for (const cluster of data.clusters) {
      cluster.paperIds = remapIndices(cluster.paperIds, idMap);
    }
    data.uncategorized = remapIndices(data.uncategorized, idMap);

    return NextResponse.json(data);
  } catch {
    return NextResponse.json(
      { error: "Failed to parse AI response" },
      { status: 502 }
    );
  }
});

