import { NextResponse } from "next/server";
import { KeyPaperSuggestionSchema } from "@/app/lib/schemas";
import { getGeminiClient, GEMINI_MODEL, buildPaperIndex } from "@/app/lib/gemini";
import { withAIObservation, withAPIObservation } from "@/app/lib/observe";

const PROMPT = `ë‹¹ì‹ ì€ í•™ìˆ  ì—°êµ¬ ì»¨ì„¤í„´íŠ¸ì…ë‹ˆë‹¤.
ì‚¬ìš©ìì˜ ê²€ìƒ‰ ì˜ë„ì™€ ë¶„ì„ëœ ë…¼ë¬¸ ëª©ë¡ì„ ë³´ê³ , ê°€ì¥ í•µì‹¬ì ì¸ ë…¼ë¬¸ 3~5ê°œë¥¼ ì„ ì •í•˜ì„¸ìš”.

ê²€ìƒ‰ ì¿¼ë¦¬: {query}
ì¿¼ë¦¬ ìœ í˜•: {queryType}

{queryTypeGuide}

[ë…¼ë¬¸ ëª©ë¡]
{papers}

ì„ ì • ê¸°ì¤€:
1. ê²€ìƒ‰ ì˜ë„ì— ê°€ì¥ ë¶€í•©í•˜ëŠ” ë…¼ë¬¸
2. í•´ë‹¹ ë¶„ì•¼ì˜ ì¤‘ìš”í•œ ê¸°ì—¬ë¥¼ í•œ ë…¼ë¬¸
3. í›„ì† ì—°êµ¬ íƒìƒ‰ì˜ ì¶œë°œì ìœ¼ë¡œ ì í•©í•œ ë…¼ë¬¸

paperIdì—ëŠ” ê° ë…¼ë¬¸ ì•ì˜ ìˆ«ì ì¸ë±ìŠ¤ë¥¼ ê·¸ëŒ€ë¡œ ì‚¬ìš©í•˜ì„¸ìš”.
ë°˜ë“œì‹œ ì•„ë˜ JSON í˜•ì‹ìœ¼ë¡œë§Œ ì‘ë‹µí•˜ì„¸ìš” (ë‹¤ë¥¸ í…ìŠ¤íŠ¸ ì—†ì´):
{
  "suggestions": [
    { "paperId": "0", "reason": "ì´ ë…¼ë¬¸ì„ í•µì‹¬ìœ¼ë¡œ ì„ ì •í•œ ê·¼ê±° (1-2ë¬¸ì¥, í•œêµ­ì–´)" }
  ]
}`;

function getQueryTypeGuide(queryType: string): string {
  if (queryType === "specific") {
    return "ì´ ê²€ìƒ‰ì€ íŠ¹ì • ì£¼ì œ/ì €ìë¥¼ ì°¾ëŠ” êµ¬ì²´ì  ê²€ìƒ‰ì…ë‹ˆë‹¤. ê²€ìƒ‰ì–´ì™€ ì •í™•íˆ ì¼ì¹˜í•˜ëŠ” ë…¼ë¬¸ì— ê°€ì¤‘ì¹˜ë¥¼ ë‘ì„¸ìš”.";
  }
  return "ì´ ê²€ìƒ‰ì€ ë„“ì€ ì£¼ì œë¥¼ íƒìƒ‰í•˜ëŠ” ë‹¨ìˆœ ê²€ìƒ‰ì…ë‹ˆë‹¤. ë¶„ì•¼ ê°œê´€ì— ì í•©í•œ ë…¼ë¬¸ê³¼ ë‹¤ì–‘í•œ ê´€ì ì„ ì»¤ë²„í•˜ëŠ” ë…¼ë¬¸ì„ ì„ íƒí•˜ì„¸ìš”.";
}

export const POST = withAPIObservation("/api/key-papers", async (request) => {
  const { query, queryType, papers } = await request.json();

  if (!query || !papers || !Array.isArray(papers)) {
    return NextResponse.json(
      { error: "query and papers array are required" },
      { status: 400 }
    );
  }

  const { idMap, paperList } = buildPaperIndex(
    papers.map((p: { paperId: string; title: string; abstract: string | null; summary?: string }) => ({
      paperId: p.paperId,
      title: p.title,
      abstract: p.summary ?? p.abstract,
    })),
    {
      formatExtra: (_p, i) =>
        ` (${papers[i].year ?? "N/A"}, ì¸ìš© ${papers[i].citationCount})`,
    }
  );

  const prompt = PROMPT
    .replace("{query}", query)
    .replace("{queryType}", queryType ?? "simple")
    .replace("{queryTypeGuide}", getQueryTypeGuide(queryType ?? "simple"))
    .replace("{papers}", paperList);

  const response = await withAIObservation("key-papers", { paperCount: papers.length }, () =>
    getGeminiClient().models.generateContent({
      model: GEMINI_MODEL,
      contents: prompt,
      config: {
        responseMimeType: "application/json",
      },
    })
  );

  const text = response.text ?? "";

  try {
    const json = JSON.parse(text);
    const parsed = KeyPaperSuggestionSchema.safeParse(json);

    if (!parsed.success) {
      return NextResponse.json(
        { error: "Invalid key paper response", details: parsed.error.issues },
        { status: 502 }
      );
    }

    // ì¸ë±ìŠ¤ â†’ ì‹¤ì œ paperId ì¹˜í™˜
    const data = parsed.data;
    data.suggestions = data.suggestions
      .filter((s) => idMap.has(s.paperId))
      .map((s) => ({ ...s, paperId: idMap.get(s.paperId)! }));

    return NextResponse.json(data);
  } catch {
    return NextResponse.json(
      { error: "Failed to parse AI response" },
      { status: 502 }
    );
  }
});

