import { getDb } from "./db";

interface MessageRow {
  id: string;
  conversation_id: string;
  type: string;
  content: Record<string, unknown>;
  created_at: string;
}

/** AI SDKì˜ UIMessageì™€ í˜¸í™˜ë˜ëŠ” í˜•íƒœë¡œ ë³€í™˜í•œë‹¤ */
export interface PersistedMessage {
  id: string;
  conversationId: string;
  role: "user" | "assistant";
  content: string;
  createdAt: string;
}

function toPersistedMessage(row: MessageRow): PersistedMessage | null {
  // í˜„ì¬ëŠ” user/assistant í…ìŠ¤íŠ¸ ë©”ì‹œì§€ë§Œ ì§€ì›
  if (row.type !== "user" && row.type !== "assistant") return null;
  const content = row.content as { text?: string };
  return {
    id: row.id,
    conversationId: row.conversation_id,
    role: row.type as "user" | "assistant",
    content: content.text ?? "",
    createdAt: row.created_at,
  };
}

export async function listMessages(
  conversationId: string,
): Promise<PersistedMessage[]> {
  const { data, error } = await getDb()
    .from("messages")
    .select("*")
    .eq("conversation_id", conversationId)
    .order("created_at", { ascending: true });

  if (error) throw error;
  return (data as MessageRow[])
    .map(toPersistedMessage)
    .filter((m): m is PersistedMessage => m !== null);
}

export async function appendMessage(params: {
  conversationId: string;
  type: "user" | "assistant";
  text: string;
}): Promise<PersistedMessage> {
  const { data, error } = await getDb()
    .from("messages")
    .insert({
      conversation_id: params.conversationId,
      type: params.type,
      content: { text: params.text },
    })
    .select()
    .single();

  if (error) throw error;
  const row = data as MessageRow;
  return toPersistedMessage(row)!;
}

