import { notFound } from "next/navigation";
import { ChatCanvas } from "@/app/components/ChatCanvas";
import {
  getConversation,
  listMessages,
} from "@/app/server/repository";
import type { UIMessage } from "@ai-sdk/react";

interface ChatPageProps {
  params: Promise<{ conversationId: string }>;
}

export default async function ChatPage({ params }: ChatPageProps) {
  const { conversationId } = await params;

  const conversation = await getConversation(conversationId);
  if (!conversation) notFound();

  const dbMessages = await listMessages(conversationId);

  // DB ë©”ì‹œì§€ë¥¼ AI SDKì˜ UIMessage í˜•íƒœë¡œ ë³€í™˜
  const initialMessages: UIMessage[] = dbMessages.map((m) => ({
    id: m.id,
    role: m.role,
    content: m.content,
    parts: [{ type: "text" as const, text: m.content }],
    createdAt: new Date(m.createdAt),
  }));

  return (
    <ChatCanvas
      conversationId={conversationId}
      initialMessages={initialMessages}
    />
  );
}

